// Check for low stock items
const products = items[0].json.data || [];
const lowStockItems = [];
const outOfStockItems = [];

for (const product of products) {
  const totalStock = product.inventory_total || 0;

  if (totalStock === 0) {
    outOfStockItems.push({
      id: product.id,
      name: `${product.categoria} - ${product.marca} ${product.modelo}`,
      sku: product.sku,
      stock: totalStock,
      locations: {
        egdc: product.inv_egdc || 0,
        fami: product.inv_fami || 0,
        bodega: product.inv_bodega_principal || 0,
        centro: product.inv_tienda_centro || 0,
        norte: product.inv_tienda_norte || 0,
        sur: product.inv_tienda_sur || 0,
        online: product.inv_online || 0
      }
    });
  } else if (totalStock <= 5) {
    lowStockItems.push({
      id: product.id,
      name: `${product.categoria} - ${product.marca} ${product.modelo}`,
      sku: product.sku,
      stock: totalStock,
      locations: {
        egdc: product.inv_egdc || 0,
        fami: product.inv_fami || 0,
        bodega: product.inv_bodega_principal || 0,
        centro: product.inv_tienda_centro || 0,
        norte: product.inv_tienda_norte || 0,
        sur: product.inv_tienda_sur || 0,
        online: product.inv_online || 0
      }
    });
  }
}

return {
  lowStock: lowStockItems,
  outOfStock: outOfStockItems,
  totalProducts: products.length,
  timestamp: new Date().toISOString()
};

