let report = `ðŸ“Š REPORTE DE INVENTARIO EGDC\n`;
report += `ðŸ•’ Fecha: ${new Date().toLocaleDateString('es-ES')}\n`;
report += `ðŸ“¦ Total productos: ${data.totalProducts}\n\n`;

if (data.outOfStock.length > 0) {
  report += `ðŸš¨ PRODUCTOS SIN STOCK (${data.outOfStock.length}):\n`;
  for (const item of data.outOfStock) {
    report += `â€¢ ${item.name} (SKU: ${item.sku})\n`;
  }
  report += `\n`;
}

if (data.lowStock.length > 0) {
  report += `âš ï¸ PRODUCTOS CON STOCK BAJO (${data.lowStock.length}):\n`;
  for (const item of data.lowStock) {
    report += `â€¢ ${item.name} (SKU: ${item.sku}) - Stock: ${item.stock}\n`;
  }
  report += `\n`;
}

report += `\nðŸ’¡ Este reporte fue generado automÃ¡ticamente por el sistema EGDC.`;

return {
  report: report,
  hasOutOfStock: data.outOfStock.length > 0,
  hasLowStock: data.lowStock.length > 0,
  alertCount: data.lowStock.length + data.outOfStock.length
};

// Log the inventory check
const data = items[0].json;

console.log(`[${new Date().toISOString()}] Inventory Check Completed`);
console.log(`Total Products: ${data.totalProducts}`);
console.log(`Low Stock Items: ${data.lowStock?.length || 0}`);
console.log(`Out of Stock Items: ${data.outOfStock?.length || 0}`);

if (data.lowStock?.length > 0) {
  console.log('Low Stock Items:');
  data.lowStock.forEach(item => {
    console.log(`  - ${item.name} (${item.sku}): ${item.stock} units`);
  });
}

if (data.outOfStock?.length > 0) {
  console.log('Out of Stock Items:');
  data.outOfStock.forEach(item => {
    console.log(`  - ${item.name} (${item.sku})`);
  });
}

return items;

// Error handling for failed API call
const error = items[0].json;

console.error(`[${new Date().toISOString()}] Inventory API Error:`, error);

return {
  error: 'Failed to fetch inventory data',
  timestamp: new Date().toISOString(),
  details: error
};

// No alerts - just log success
console.log(`[${new Date().toISOString()}] Inventory check completed - No alerts`);

return {
  message: 'Inventory check completed successfully',
  timestamp: new Date().toISOString(),
  totalProducts: items[0].json.totalProducts
};
